    /*
     * Adding a new contact_info
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'STATUT' => $this->input->post('STATUT'),
				'NOM' => $this->input->post('NOM'),
				'TEXTE' => $this->input->post('TEXTE'),
				'CLASS' => $this->input->post('CLASS'),
            );
            
            $contact_info_id = $this->Contact_info_model->add_contact_info($params);
            redirect('contact_info/index');
        }
        else
        {            
            $data['_view'] = 'contact_info/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a contact_info
     */
    function edit($ID)
    {   
        // check if the contact_info exists before trying to edit it
        $data['contact_info'] = $this->Contact_info_model->get_contact_info($ID);
        
        if(isset($data['contact_info']['ID']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'STATUT' => $this->input->post('STATUT'),
					'NOM' => $this->input->post('NOM'),
					'TEXTE' => $this->input->post('TEXTE'),
					'CLASS' => $this->input->post('CLASS'),
                );

                $this->Contact_info_model->update_contact_info($ID,$params);            
                redirect('contact_info/index');
            }
            else
