    } 

    /*
     * Deleting contact_info
     */
    function remove($ID)
    {
        $contact_info = $this->Contact_info_model->get_contact_info($ID);

        // check if the contact_info exists before trying to delete it
        if(isset($contact_info['ID']))
        {
            $this->Contact_info_model->delete_contact_info($ID);
            redirect('contact_info/index');
        }
        else
