    /*
     * Adding a new exercice
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'STATUT' => $this->input->post('STATUT'),
				'IDIMAGE' => $this->input->post('IDIMAGE'),
				'NOM' => $this->input->post('NOM'),
				'DESCRIPTION' => $this->input->post('DESCRIPTION'),
            );
            
            $exercice_id = $this->Exercice_model->add_exercice($params);
            redirect('exercice/index');
        }
        else
        {            
            $data['_view'] = 'exercice/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a exercice
     */
    function edit($ID)
    {   
        // check if the exercice exists before trying to edit it
        $data['exercice'] = $this->Exercice_model->get_exercice($ID);
        
        if(isset($data['exercice']['ID']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'STATUT' => $this->input->post('STATUT'),
					'IDIMAGE' => $this->input->post('IDIMAGE'),
					'NOM' => $this->input->post('NOM'),
					'DESCRIPTION' => $this->input->post('DESCRIPTION'),
                );

                $this->Exercice_model->update_exercice($ID,$params);            
                redirect('exercice/index');
            }
            else
            {
                $data['_view'] = 'exercice/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
