    } 

    /*
     * Deleting exercice
     */
    function remove($ID)
    {
        $exercice = $this->Exercice_model->get_exercice($ID);

        // check if the exercice exists before trying to delete it
        if(isset($exercice['ID']))
        {
            $this->Exercice_model->delete_exercice($ID);
            redirect('exercice/index');
        }
        else
